import {
    Typography, List, ListSubheader,
} from '@material-ui/core';
import axios from 'axios';
import React from 'react';
import LinearProgress from '@material-ui/core/LinearProgress';
import AuthContext from '../../AuthContext';
import { extractUId } from '../../utils/token';
import Message from '../Message';

function Search({ query_str, ...props }) {

    const [loading, setLoading] = React.useState(true);
    const [messages, setMessages] = React.useState([]);

    const token = React.useContext(AuthContext);
    const u_id = extractUId(token);

    function performSearch() {
        axios
        .get('/search', {
            params: {
                token,
                query_str,
            },
        })
        .then(({ data }) => {
            const { messages } = data;
            if (typeof messages === "undefined" || !Array.isArray(messages)) return;
            setMessages(messages)
            setLoading(false);
        })
        .catch((err) => {
            setLoading(false);
        });
    }

    // Only perform search if there is a query
    React.useEffect(() => {
        if (query_str !== "") performSearch();
    }, [token, query_str])

    // If there is no query
    if (query_str === "") {
        return <>
            <Typography variant="h4" style={{marginBottom: 10}}>Search</Typography>
            <Typography variant="h6">Enter a query in the search bar above ğŸ‘€</Typography>
        </>
    }

    // If there is a query
    return <>
        <Typography variant="h4" style={{marginBottom: 10}}>Search Results</Typography>
        {(loading) ?
            <LinearProgress />
        :
            (messages.length == 0) ?
                <Typography variant="h6">Your search did not return any results ğŸ™</Typography>
            :
                <List
                    subheader={<ListSubheader>Messages</ListSubheader>}
                    style={{ width: '100%' }}
                >
                {messages.slice().reverse().map((message) => (
                    <Message key={message.message_id} {...message} />
                ))}
                </List>
            }
    </>
}

export default Search;
